tables.table_schema:
CREATE TABLE course_material (
    course_id INT PRIMARY KEY,
    course_name TEXT,
    lecture_notes BYTEA,
    presentations BYTEA
);

CREATE TABLE LectureNotes ( 
  id INT PRIMARY KEY, 
  course_id INT, 
  lecture_title VARCHAR, 
  lecture_content TEXT 
);

CREATE TABLE Presentations ( 
  id INT PRIMARY KEY, 
  course_id INT, 
  presentation_title VARCHAR, 
  presentation_file BLOB 
);

CREATE TABLE LectureNotes (
    id INT PRIMARY KEY,
    course_id INT,
    note_content TEXT,
    created_at TIMESTAMP
);

CREATE TABLE LectureNotes (
    note_id INT PRIMARY KEY,
    course_id INT,
    note_title VARCHAR(255),
    note_content TEXT
);

CREATE TABLE Presentations (
    presentation_id INT PRIMARY KEY,
    course_id INT,
    presentation_title VARCHAR(255),
    presentation_file_path VARCHAR(255)
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(255)
);

CREATE TABLE CourseMaterials (
    material_id INT PRIMARY KEY,
    course_id INT,
    material_type VARCHAR(50),
    material_description TEXT
);

CREATE TABLE CourseMaterial (
    course_id UUID PRIMARY KEY,
    course_name TEXT,
    lecture_notes TEXT,
    presentations BYTEA
);

CREATE TABLE Lecturers (
    lecturer_id UUID PRIMARY KEY,
    lecturer_name TEXT,
    department TEXT
);

CREATE TABLE Students (
    student_id UUID PRIMARY KEY,
    student_name TEXT,
    major TEXT
);

CREATE TABLE Enrollments (
    student_id UUID,
    course_id UUID,
    enroll_date TIMESTAMP,
    PRIMARY KEY (student_id, course_id)
);

CREATE TABLE Assignments (
    assignment_id UUID PRIMARY KEY,
    course_id UUID,
    assignment_desc TEXT,
    due_date TIMESTAMP
);
