tables.table_schema:
CREATE TABLE CourseMaterials (
  course_id INT PRIMARY KEY,
  course_name VARCHAR(255),
  course_description TEXT
);

CREATE TABLE Courses (
  course_id UUID PRIMARY KEY,
  course_name TEXT NOT NULL,
  course_description TEXT,
  course_level INT
);

CREATE TABLE Materials (
  material_id UUID PRIMARY KEY,
  material_name TEXT NOT NULL,
  material_type TEXT,
  course_id UUID REFERENCES Courses(course_id)
);

CREATE TABLE Students (
  student_id UUID PRIMARY KEY,
  student_name TEXT NOT NULL,
  student_email TEXT,
  student_course_id UUID REFERENCES Courses(course_id)
);

CREATE TABLE Course (course_id INT PRIMARY KEY, course_name VARCHAR(255) NOT NULL, course_description TEXT);
CREATE TABLE Material (material_id INT PRIMARY KEY, material_name VARCHAR(255) NOT NULL, material_type VARCHAR(50), course_id INT NOT NULL,
FOREIGN KEY (course_id) REFERENCES Course(course_id) ON DELETE CASCADE);
CREATE TABLE Chapter (chapter_id INT PRIMARY KEY, chapter_title VARCHAR(255) NOT NULL, course_id INT NOT NULL,
FOREIGN KEY (course_id) REFERENCES Course(course_id) ON DELETE CASCADE);
CREATE TABLE Lesson (lesson_id INT PRIMARY KEY, lesson_title VARCHAR(255) NOT NULL, chapter_id INT NOT NULL,
FOREIGN KEY (chapter_id) REFERENCES Chapter(chapter_id) ON DELETE CASCADE);
CREATE TABLE Resource (resource_id INT PRIMARY KEY, resource_name VARCHAR(255) NOT NULL, resource_type VARCHAR(50), lesson_id INT NOT NULL,
FOREIGN KEY (lesson_id) REFERENCES Lesson(lesson_id) ON DELETE CASCADE);
