CREATE TABLE lecture_notes (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    course_id INT
);

CREATE TABLE slides (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    image_url VARCHAR(255),
    course_id INT
);

CREATE TABLE study_materials (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    material_type VARCHAR(50),
    course_id INT
);

