tables.table_schema:
CREATE TABLE curriculum_content (
  content_id INT PRIMARY KEY,
  content_title VARCHAR(255),
  content_description TEXT,
  content_type VARCHAR(50)
);

CREATE TABLE curriculum_content (
    content_id UUID PRIMARY KEY,
    content_name VARCHAR(255) NOT NULL,
    content_description TEXT
);

CREATE TABLE curriculum_content (
  content_id INT PRIMARY KEY,
  content_title VARCHAR(255),
  content_description TEXT
);

CREATE TABLE curriculum (
  curriculum_id UUID PRIMARY KEY,
  curriculum_name VARCHAR(255) NOT NULL,
  curriculum_description TEXT
);

CREATE TABLE lesson_plans (
  lesson_plan_id UUID PRIMARY KEY,
  lesson_plan_title VARCHAR(255) NOT NULL,
  lesson_plan_description TEXT,
  curriculum_id UUID REFERENCES curriculum(curriculum_id)
);

CREATE TABLE assessments (
  assessment_id UUID PRIMARY KEY,
  assessment_title VARCHAR(255) NOT NULL,
  assessment_description TEXT,
  lesson_plan_id UUID REFERENCES lesson_plans(lesson_plan_id)
);

CREATE TABLE students (
  student_id UUID PRIMARY KEY,
  student_name VARCHAR(255) NOT NULL,
  student_grade VARCHAR(10),
  lesson_plan_id UUID REFERENCES lesson_plans(lesson_plan_id)
);

CREATE TABLE teachers (
  teacher_id UUID PRIMARY KEY,
  teacher_name VARCHAR(255) NOT NULL,
  teacher_email VARCHAR(255),
  curriculum_id UUID REFERENCES curriculum(curriculum_id)
);
