tables.table_schema:
CREATE TABLE course_materials (
    course_id INT PRIMARY KEY,
    lecture_slides BYTEA,
    videos BYTEA,
    quizzes BYTEA
);

CREATE TABLE lecture_materials (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    slides_url VARCHAR(255),
    video_url VARCHAR(255),
    course_id INT
);

CREATE TABLE quiz_materials (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    questions JSONB,
    course_id INT
);

CREATE TABLE lecture_slides (
    slide_id UUID PRIMARY KEY,
    course_id UUID,
    slide_number INT,
    slide_file BYTEA
);

CREATE TABLE quizzes (
    quiz_id UUID PRIMARY KEY,
    course_id UUID,
    quiz_title TEXT,
    quiz_questions JSONB
);

CREATE TABLE course_materials (
    material_id UUID PRIMARY KEY,
    course_id UUID,
    material_type TEXT,
    material_file BYTEA
);

CREATE TABLE LectureSlides (
    slide_id INT PRIMARY KEY,
    course_id INT,
    slide_title TEXT,
    slide_content BYTEA
);

CREATE TABLE Videos (
    video_id INT PRIMARY KEY,
    course_id INT,
    video_title TEXT,
    video_content BYTEA
);

CREATE TABLE Quizzes (
    quiz_id INT PRIMARY KEY,
    course_id INT,
    quiz_title TEXT,
    quiz_questions JSONB
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name TEXT,
    instructor TEXT,
    start_date DATE
);

CREATE TABLE CourseMaterials (
    material_id INT PRIMARY KEY,
    course_id INT,
    material_type TEXT,
    material_description TEXT
);
