);

CREATE TABLE event_managers (
    manager_id SERIAL PRIMARY KEY,
    manager_name VARCHAR(100) NOT NULL,
    manager_email VARCHAR(100) NOT NULL,
    venue_id INT REFERENCES venues(venue_id)
);
