tables.table_schema:
CREATE TABLE departments (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    manager VARCHAR(255)
);

CREATE TABLE instruments (
  id UUID PRIMARY KEY,
  name STRING,
  brand STRING,
  category STRING
);

CREATE TABLE equipment (
  id UUID PRIMARY KEY,
  name STRING,
  brand STRING,
  type STRING
);

CREATE TABLE inventory (
  id UUID PRIMARY KEY,
  item_id UUID,
  quantity INT,
  price DECIMAL
);

CREATE TABLE sales (
  id UUID PRIMARY KEY,
  item_id UUID,
  quantity INT,
  sale_date DATE
);
