tables.table_schema:
CREATE TABLE music_streaming_stats (
    id INT PRIMARY KEY,
    song_name VARCHAR,
    artist_name VARCHAR,
    stream_count INT
);

CREATE TABLE user_engagement_metrics (
    user_id INT PRIMARY KEY,
    total_streams INT,
    total_likes INT,
    last_streamed_at TIMESTAMP
);

CREATE TABLE song_statistics (
  song_id UUID,
  num_streams INT,
  num_likes INT
);

CREATE TABLE user_statistics (
  user_id UUID,
  num_login INT,
  num_likes_given INT
);

CREATE TABLE user_activity (
  user_id UUID,
  song_id UUID,
  action_type VARCHAR(50),
  action_timestamp TIMESTAMP
);

CREATE TABLE song_streams (
    song_id UUID PRIMARY KEY,
    user_id UUID,
    stream_count INT
);

CREATE TABLE user_profile (
    user_id UUID PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    date_registered TIMESTAMP
);

CREATE TABLE artist_engagement (
    artist_id UUID PRIMARY KEY,
    total_streams INT,
    total_followers INT
);

CREATE TABLE playlist_data (
    playlist_id UUID PRIMARY KEY,
    user_id UUID,
    total_songs INT,
    date_created TIMESTAMP
);

CREATE TABLE song_statistics (
    song_id INT PRIMARY KEY,
    plays INT,
    likes INT,
    downloads INT
);

CREATE TABLE user_statistics (
    user_id INT PRIMARY KEY,
    total_plays INT,
    total_likes INT,
    total_downloads INT
);

CREATE TABLE artist_statistics (
    artist_id INT PRIMARY KEY,
    total_plays INT,
    total_likes INT,
    total_downloads INT
);

CREATE TABLE playlists (
    playlist_id INT PRIMARY KEY,
    playlist_name VARCHAR(255),
    total_songs INT
);

CREATE TABLE user_playlist_mapping (
    user_id INT,
    playlist_id INT,
    PRIMARY KEY (user_id, playlist_id)
);
