tables.table_schema:
CREATE TABLE museums (
    museum_id INT PRIMARY KEY,
    museum_name VARCHAR(100) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE art_museums (
  museum_id INT PRIMARY KEY,
  museum_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE ArtMuseums (
    museum_id INT PRIMARY KEY,
    museum_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE ArtCollections (
    collection_id INT PRIMARY KEY,
    collection_name VARCHAR(50) NOT NULL,
    museum_id INT NOT NULL,
    FOREIGN KEY (museum_id) REFERENCES ArtMuseums(museum_id)
);

CREATE TABLE ArtArtists (
    artist_id INT PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL
);

CREATE TABLE Museum(
  museum_id INT PRIMARY KEY,
  museum_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE Artist(
  artist_id INT PRIMARY KEY,
  artist_name VARCHAR(50) NOT NULL,
  nationality VARCHAR(50)
);

CREATE TABLE Exhibition(
  exhibition_id INT PRIMARY KEY,
  exhibition_name VARCHAR(100) NOT NULL,
  start_date DATE,
  end_date DATE
);

CREATE TABLE Visitor(
  visitor_id INT PRIMARY KEY,
  visitor_name VARCHAR(50) NOT NULL,
  email VARCHAR(100)
);
