tables.table_schema:
CREATE TABLE Artists (
  artist_id SERIAL PRIMARY KEY,
  artist_name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE,
  birthdate DATE
);

CREATE TABLE Supplies (
  supply_id SERIAL PRIMARY KEY,
  supply_name VARCHAR(50) NOT NULL,
  supplier VARCHAR(100),
  price DECIMAL(10, 2)
);

CREATE TABLE artists (
    artist_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL,
    artist_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE artworks (
    artwork_id SERIAL PRIMARY KEY,
    artwork_name VARCHAR(100) NOT NULL,
    artist_id INT REFERENCES artists(artist_id) NOT NULL
);

CREATE TABLE materials (
    material_id SERIAL PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    material_type VARCHAR(50) NOT NULL
);

CREATE TABLE supplies (
    supply_id SERIAL PRIMARY KEY,
    supply_name VARCHAR(50) NOT NULL,
    supply_quantity INT NOT NULL
);

CREATE TABLE artwork_materials (
    artwork_id INT REFERENCES artworks(artwork_id) NOT NULL,
    material_id INT REFERENCES materials(material_id) NOT NULL,
    quantity_used INT NOT NULL
);
