tables.table_schema:
CREATE TABLE DanceCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE DancerSkills (
    skill_id INT PRIMARY KEY,
    dancer_id INT,
    skill_name VARCHAR(50),
    proficiency_level VARCHAR(20)
);

CREATE TABLE DancerProfiles (
    dancer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE Skills (
    skill_id INT PRIMARY KEY,
    skill_name VARCHAR(50),
    dancer_id INT,
    FOREIGN KEY (dancer_id) REFERENCES DancerProfiles(dancer_id)
);

CREATE TABLE Experience (
    exp_id INT PRIMARY KEY,
    experience_description TEXT,
    dancer_id INT,
    FOREIGN KEY (dancer_id) REFERENCES DancerProfiles(dancer_id)
);

CREATE TABLE skills (
	skill_id INT PRIMARY KEY,
	skill_name VARCHAR(50),
	proficiency_level ENUM('beginner', 'intermediate', 'advanced')
);

CREATE TABLE performances (
	perf_id INT PRIMARY KEY,
	perf_title VARCHAR(100),
	perf_date DATE,
	venue VARCHAR(255)
);

CREATE TABLE awards (
	award_id INT PRIMARY KEY,
	award_title VARCHAR(100),
	award_date DATE,
	award_description TEXT
);
