tables.table_schema:
CREATE TABLE festival_lineups (
   lineup_id INT PRIMARY KEY,
   lineup_name VARCHAR(255) NOT NULL,
   festival_date DATE NOT NULL
);

CREATE TABLE festival_lineup (
    lineup_id SERIAL PRIMARY KEY,
    festival_name VARCHAR(255) NOT NULL,
    festival_date DATE NOT NULL
);

CREATE TABLE performance (
    performance_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    performance_type VARCHAR(100) NOT NULL,
    lineup_id INT REFERENCES festival_lineup(lineup_id)
);

CREATE TABLE lineup (
    lineup_id SERIAL PRIMARY KEY,
    festival_id INT NOT NULL,
    performance_date DATE NOT NULL,
    performance_time TIME NOT NULL
);

CREATE TABLE performance (
    performance_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL,
    performance_type VARCHAR(50) NOT NULL,
    lineup_id INT NOT NULL
);

CREATE TABLE collaboration (
    collaboration_id SERIAL PRIMARY KEY,
    artist1_name VARCHAR(50) NOT NULL,
    artist2_name VARCHAR(50) NOT NULL,
    collaboration_type VARCHAR(50) NOT NULL
);

CREATE TABLE Lineups (
    lineup_id SERIAL PRIMARY KEY,
    lineup_date DATE NOT NULL,
    festival_name TEXT NOT NULL
);

CREATE TABLE Performances (
    performance_id SERIAL PRIMARY KEY,
    performance_name TEXT NOT NULL,
    performer_name TEXT NOT NULL,
    performance_date DATE NOT NULL
);

CREATE TABLE Collaborations (
    collaboration_id SERIAL PRIMARY KEY,
    primary_performer TEXT NOT NULL,
    secondary_performer TEXT NOT NULL,
    collaboration_date DATE NOT NULL
);

CREATE TABLE Locations (
    location_id SERIAL PRIMARY KEY,
    location_name TEXT NOT NULL,
    city TEXT NOT NULL,
    country TEXT NOT NULL
);

CREATE TABLE Tickets (
    ticket_id SERIAL PRIMARY KEY,
    ticket_type TEXT NOT NULL,
    ticket_price DECIMAL(10, 2) NOT NULL,
    ticket_quantity INT NOT NULL
);
