tables.table_schema:
CREATE TABLE costumes (
    costume_id INTEGER PRIMARY KEY,
    costume_name VARCHAR(255),
    costume_type VARCHAR(50),
    quantity INTEGER
);

CREATE TABLE alterations (
    alteration_id INTEGER PRIMARY KEY,
    costume_id INTEGER,
    alteration_date DATE,
    description TEXT
);

CREATE TABLE orders (
    order_id INTEGER PRIMARY KEY,
    customer_name VARCHAR(100),
    order_date DATE,
    delivery_address TEXT
);

CREATE TABLE costume_orders (
    order_id INTEGER,
    costume_id INTEGER,
    quantity INTEGER
);

CREATE TABLE Orders (
  order_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  order_date DATE,
  order_status VARCHAR(50)
);
