tables.table_schema:
CREATE TABLE dance_team_routines (
  routine_id INT PRIMARY KEY,
  routine_name VARCHAR(255) NOT NULL,
  formation_id INT,
  music_playlist_id INT
);

CREATE TABLE `dance_routines` (
  `routine_id` INT PRIMARY KEY,
  `routine_name` VARCHAR(50) NOT NULL,
  `formation_id` INT,
  `playlist_id` INT
);

CREATE TABLE `formations` (
  `formation_id` INT PRIMARY KEY,
  `formation_name` VARCHAR(50) NOT NULL,
  `routine_id` INT,
  `dancer_count` INT
);

CREATE TABLE dance_routines (
    routine_id INT PRIMARY KEY,
    routine_name VARCHAR(50),
    formation_id INT,
    playlist_id INT
);

CREATE TABLE formations (
    formation_id INT PRIMARY KEY,
    formation_name VARCHAR(50),
    formation_description TEXT
);

CREATE TABLE playlists (
    playlist_id INT PRIMARY KEY,
    playlist_name VARCHAR(50),
    music_genre VARCHAR(50)
);

CREATE TABLE routine_steps (
    step_id INT PRIMARY KEY,
    step_description TEXT,
    routine_id INT,
    step_order INT
);
