tables.table_schema:
CREATE TABLE actors (
  actor_id UUID PRIMARY KEY,
  first_name STRING,
  last_name STRING,
  date_of_birth DATE
);

CREATE TABLE Actors (
  actor_id INT PRIMARY KEY,
  actor_name VARCHAR(255),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE PreviousPerformances (
  performance_id INT PRIMARY KEY,
  actor_id INT,
  performance_date DATE,
  performance_title VARCHAR(255)
);

CREATE TABLE FutureRolesAvailability (
  availability_id INT PRIMARY KEY,
  actor_id INT,
  role_title VARCHAR(255),
  availability_status VARCHAR(20)
);
