tables.table_schema:
CREATE TABLE theater_memberships (
    membership_id UUID PRIMARY KEY,
    member_name VARCHAR(255) NOT NULL,
    membership_level VARCHAR(50) NOT NULL,
    expiry_date DATE NOT NULL
);

CREATE TABLE theater_benefits (
    benefit_id UUID PRIMARY KEY,
    benefit_name VARCHAR(100) NOT NULL,
    description TEXT,
    membership_level VARCHAR(50) NOT NULL
);

CREATE TABLE theater_memberships (
    membership_id SERIAL PRIMARY KEY,
    member_name TEXT,
    membership_start_date DATE,
    membership_expiry_date DATE
);

CREATE TABLE benefits (
    benefit_id SERIAL PRIMARY KEY,
    benefit_description TEXT,
    benefit_type TEXT
);

CREATE TABLE expiry_dates (
    expiry_id SERIAL PRIMARY KEY,
    membership_id INT,
    expiry_date DATE
);

CREATE TABLE benefits (
    benefit_id INT PRIMARY KEY,
    benefit_description VARCHAR(255),
    membership_type VARCHAR(50),
    benefit_value FLOAT
);

CREATE TABLE expiry_dates (
    expiry_id INT PRIMARY KEY,
    membership_id INT,
    expiry_date DATE,
    FOREIGN KEY (membership_id) REFERENCES theater_memberships(membership_id)
);

CREATE TABLE theaters (
    theater_id INT PRIMARY KEY,
    theater_name VARCHAR(100),
    location VARCHAR(100),
    theater_capacity INT
);
