tables.table_schema:
CREATE TABLE performer_lineup (
    performer_id INT PRIMARY KEY,
    performer_name VARCHAR(255),
    performance_date DATE
);

CREATE TABLE showtimes (
    showtime_id INT PRIMARY KEY,
    performer_id INT,
    showtime DATETIME
);

CREATE TABLE Performers (
    performer_id INT PRIMARY KEY,
    performer_name VARCHAR(50) NOT NULL,
    performer_type VARCHAR(50),
    performance_fee DECIMAL(10, 2)
);

CREATE TABLE Shows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(50) NOT NULL,
    show_date date,
    show_time time
);

CREATE TABLE Performers (
    performer_id INT PRIMARY KEY,
    performer_name VARCHAR(255),
    performer_genre VARCHAR(50)
);

CREATE TABLE Venues (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(255),
    venue_location VARCHAR(100)
);
