tables.table_schema:
CREATE TABLE comedy_specials (
id SERIAL PRIMARY KEY,
name VARCHAR(100) NOT NULL,
category VARCHAR(50) NOT NULL,
release_year INT NOT NULL
);

CREATE TABLE users (
id SERIAL PRIMARY KEY,
username VARCHAR(50) NOT NULL,
email VARCHAR(100) NOT NULL
);

CREATE TABLE user_preferences (
   user_id INT,
   genre_preference VARCHAR(50),
   FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE User (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE ComedySpecial (
    special_id SERIAL PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    release_date DATE,
    description TEXT
);

CREATE TABLE Genre (
    genre_id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL
);

CREATE TABLE UserComedySpecial (
    user_id INT REFERENCES User(user_id),
    special_id INT REFERENCES ComedySpecial(special_id),
    rating INT,
    PRIMARY KEY (user_id, special_id)
);

CREATE TABLE SpecialGenre (
    special_id INT REFERENCES ComedySpecial(special_id),
    genre_id INT REFERENCES Genre(genre_id),
    PRIMARY KEY (special_id, genre_id)
);
