CREATE TABLE jokes ( 
  id INT PRIMARY KEY, 
  joke_text VARCHAR(255), 
  writer_name VARCHAR(50), 
  submission_date TIMESTAMP 
);

CREATE TABLE jokes (
  id INT PRIMARY KEY,
  joke_text TEXT,
  author VARCHAR(50),
  likes INT
);

CREATE TABLE ideas (
  id INT PRIMARY KEY,
  idea_text TEXT,
  author VARCHAR(50),
  category VARCHAR(50)
);

CREATE TABLE Writer (writer_id INT PRIMARY KEY, writer_name VARCHAR(255));
CREATE TABLE Jokes (joke_id INT PRIMARY KEY, joke_text TEXT, writer_id INT, FOREIGN KEY (writer_id) REFERENCES Writer(writer_id));
CREATE TABLE Ideas (idea_id INT PRIMARY KEY, idea_description TEXT, writer_id INT, FOREIGN KEY (writer_id) REFERENCES Writer(writer_id));

CREATE TABLE Jokes (
   id INT PRIMARY KEY,
   joke_content TEXT,
   author_name VARCHAR(50)
);

CREATE TABLE Ideas (
   id INT PRIMARY KEY,
   idea_description TEXT,
   author_name VARCHAR(50)
);

CREATE TABLE Writers (
   id INT PRIMARY KEY,
   writer_name VARCHAR(50),
   writer_email VARCHAR(100) UNIQUE
);

CREATE TABLE Collaboration (
   joke_id INT,
   idea_id INT,
   FOREIGN KEY (joke_id) REFERENCES Jokes(id),
   FOREIGN KEY (idea_id) REFERENCES Ideas(id)
);
