tables.table_schema:
CREATE TABLE comedian_contracts (
  contract_id SERIAL PRIMARY KEY,
  comedian_id INT,
  start_date DATE,
  end_date DATE
);

CREATE TABLE bookings (
  booking_id SERIAL PRIMARY KEY,
  contract_id INT,
  venue_id INT,
  booking_date DATE
);

CREATE TABLE Comedians (
    comedian_id UUID PRIMARY KEY,
    comedian_name VARCHAR(50),
    comedian_phone VARCHAR(20),
    comedian_email VARCHAR(50)
);

CREATE TABLE TourSchedules (
    schedule_id UUID PRIMARY KEY,
    comedian_id UUID REFERENCES Comedians(comedian_id),
    tour_date DATE,
    tour_city VARCHAR(50)
);

CREATE TABLE Events (
    event_id UUID PRIMARY KEY,
    booking_id UUID REFERENCES Bookings(booking_id),
    schedule_id UUID REFERENCES TourSchedules(schedule_id),
    event_date DATE
);
