tables.table_schema:
CREATE TABLE class_schedule (
    class_id SERIAL PRIMARY KEY,
    class_date DATE NOT NULL,
    start_time TIME NOT NULL,
    end_time TIME NOT NULL
);

CREATE TABLE participant_info (
    participant_id SERIAL PRIMARY KEY,
    full_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE,
    phone_number VARCHAR(15)
);

CREATE TABLE feedback_form (
    feedback_id SERIAL PRIMARY KEY,
    participant_id INT REFERENCES participant_info(participant_id),
    class_id INT REFERENCES class_schedule(class_id),
    feedback_text TEXT NOT NULL
);

CREATE TABLE workshop_location (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE class_schedules (
    class_id SERIAL PRIMARY KEY,
    class_date DATE,
    class_time TIME,
    class_duration INTERVAL
);

CREATE TABLE participants (
    participant_id SERIAL PRIMARY KEY,
    participant_name VARCHAR(50),
    participant_email VARCHAR(50),
    participant_phone VARCHAR(15)
);

CREATE TABLE instructors (
    instructor_id SERIAL PRIMARY KEY,
    instructor_name VARCHAR(50),
    instructor_email VARCHAR(50),
    instructor_phone VARCHAR(15)
);

CREATE TABLE class_rooms (
    room_id SERIAL PRIMARY KEY,
    room_name VARCHAR(50),
    max_capacity INT
);
