tables.table_schema:
CREATE TABLE movies (
    movie_id INT PRIMARY KEY,
    movie_title VARCHAR(255) NOT NULL,
    release_year INT,
    rating DECIMAL(3,1)
);

CREATE TABLE movie_genre (movie_id INT,
  genre_id INT,
  PRIMARY KEY (movie_id, genre_id));

CREATE TABLE Actors (actor_id INT,
                 actor_name VARCHAR(255),
                 birth_date DATE);

CREATE TABLE Directors (director_id INT,
                    director_name VARCHAR(255),
                    birth_date DATE);
