CREATE TABLE upcoming_movie_releases (
  movie_id INT PRIMARY KEY,
  movie_title VARCHAR(255),
  release_date DATE,
  lead_actress VARCHAR(100)
);

CREATE TABLE upcoming_movies (
    movie_id SERIAL PRIMARY KEY,
    movie_title VARCHAR(255) NOT NULL,
    actress VARCHAR(100) NOT NULL,
    release_date DATE NOT NULL
);

CREATE TABLE actress_bio (
    actress_id SERIAL PRIMARY KEY,
    actress_name VARCHAR(100) NOT NULL,
    biography TEXT
);

CREATE TABLE upcoming_movies (
  movie_id INT PRIMARY KEY,
  movie_title VARCHAR(255),
  release_date DATE,
  actress_name VARCHAR(100)
);

CREATE TABLE actress_details (
  actress_id INT PRIMARY KEY,
  actress_name VARCHAR(100),
  birthdate DATE,
  nationality VARCHAR(100)
);

CREATE TABLE upcoming_movies (
    movie_id SERIAL PRIMARY KEY,
    movie_title VARCHAR(255),
    release_date DATE,
    actress_name VARCHAR(100)
);

CREATE TABLE actress (
    actress_id SERIAL PRIMARY KEY,
    actress_name VARCHAR(100),
    age INT,
    nationality VARCHAR(50)
);

CREATE TABLE movie_reviews (
    review_id SERIAL PRIMARY KEY,
    movie_id INT REFERENCES upcoming_movies(movie_id),
    review_text TEXT,
    rating FLOAT
);

CREATE TABLE movie_ratings (
    rating_id SERIAL PRIMARY KEY,
    movie_id INT REFERENCES upcoming_movies(movie_id),
    user_id INT,
    user_rating FLOAT
);
