    player_level INT,
    player_experience INT
);

CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR,
    level INT,
    experience INT
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    game_name VARCHAR
);

CREATE TABLE player_inventory (
    player_id INT,
    item_id INT,
    quantity INT,
    FOREIGN KEY (player_id) REFERENCES player_profiles(player_id)
);

CREATE TABLE game_progress (
    progress_id SERIAL PRIMARY KEY,
    player_id INT REFERENCES player_profiles(player_id),
    level INT NOT NULL,
    score INT DEFAULT 0
);

CREATE TABLE items_inventory (
    item_id SERIAL PRIMARY KEY,
    player_id INT REFERENCES player_profiles(player_id),
    item_name VARCHAR(50) NOT NULL,
    quantity INT DEFAULT 0
);

CREATE TABLE friends_list (
    friend_id SERIAL PRIMARY KEY,
    player_id INT REFERENCES player_profiles(player_id),
    friend_player_id INT REFERENCES player_profiles(player_id)
);

CREATE TABLE messages (
    message_id SERIAL PRIMARY KEY,
    sender_id INT REFERENCES player_profiles(player_id),
    receiver_id INT REFERENCES player_profiles(player_id),
    message_text TEXT NOT NULL
);
