tables.table_schema:
CREATE TABLE leaderboards (
  player_id INT,
  player_name VARCHAR(50),
  score INT,
  ranking INT
);

CREATE TABLE PlayerScores (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    score INT
);

CREATE TABLE Rankings (
    rank_id INT PRIMARY KEY,
    player_id INT,
    rank_position INT,
    FOREIGN KEY (player_id) REFERENCES PlayerScores(player_id)
);

CREATE TABLE player_scores (
    player_id INT PRIMARY KEY,
    score INT
);

CREATE TABLE player_rankings (
    player_id INT PRIMARY KEY,
    rank INT
);

CREATE TABLE leaderboard (
    player_id INT PRIMARY KEY,
    score INT,
    rank INT
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_email VARCHAR(50)
);

CREATE TABLE scores (
    score_id INT PRIMARY KEY,
    player_id INT,
    score DECIMAL(10,2)
);

CREATE TABLE rankings (
    ranking_id INT PRIMARY KEY,
    player_id INT,
    overall_rank INT
);

CREATE TABLE leaderboards (
    leaderboard_id INT PRIMARY KEY,
    ranking_id INT,
    leaderboard_name VARCHAR(50)
);

CREATE TABLE players (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(50) NOT NULL
);

CREATE TABLE player_scores (
    score_id UUID PRIMARY KEY,
    player_id UUID REFERENCES players(player_id),
    score INTEGER NOT NULL
);

CREATE TABLE player_rankings (
    ranking_id UUID PRIMARY KEY,
    player_id UUID REFERENCES players(player_id),
    ranking INTEGER NOT NULL
);

CREATE TABLE games (
    game_id UUID PRIMARY KEY,
    game_name VARCHAR(50) NOT NULL
);

CREATE TABLE player_games (
    player_id UUID REFERENCES players(player_id),
    game_id UUID REFERENCES games(game_id),
    player_score INTEGER NOT NULL,
    player_ranking INTEGER NOT NULL
);
