tables.table_schema:
CREATE TABLE PlayerFeedback (
  player_id INT,
  game_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE GameImprovements (
    improvement_id INT PRIMARY KEY,
    game_id INT,
    improvement_text TEXT,
    implementation_date DATE
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_email VARCHAR(50),
    registration_date DATE
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    game_title VARCHAR(50),
    game_genre VARCHAR(50),
    release_date DATE
);

CREATE TABLE Players (
    player_id UUID PRIMARY KEY,
    player_name TEXT,
    email TEXT,
    date_joined DATE
);

CREATE TABLE Games (
    game_id UUID PRIMARY KEY,
    game_name TEXT,
    release_date DATE,
    genre TEXT
);

CREATE TABLE GameFeedback (
    game_id UUID,
    feedback_id UUID,
    CONSTRAINT fk_game_id FOREIGN KEY (game_id) REFERENCES Games(game_id),
    CONSTRAINT fk_feedback_id FOREIGN KEY (feedback_id) REFERENCES Feedback(feedback_id)
);
