tables.table_schema:
CREATE TABLE episode_ratings (
  episode_id INT,
  viewer_id INT,
  rating FLOAT,
  review TEXT
);

CREATE TABLE Episode (episode_id UUID PRIMARY KEY,
                          episode_title VARCHAR(255) NOT NULL,
                          air_date DATE NOT NULL
                         );

CREATE TABLE Viewer (viewer_id UUID PRIMARY KEY,
                         viewer_name VARCHAR(255) NOT NULL,
                         email VARCHAR(255) UNIQUE
                         );

CREATE TABLE Rating (rating_id UUID PRIMARY KEY,
                         episode_id UUID REFERENCES Episode(episode_id),
                         viewer_id UUID REFERENCES Viewer(viewer_id),
                         rating FLOAT NOT NULL,
                         review TEXT
                         );

CREATE TABLE episodes (
    episode_id UUID PRIMARY KEY,
    episode_title STRING,
    viewer_rating FLOAT,
    viewer_review STRING
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username STRING,
    email STRING
);

CREATE TABLE ratings (
    rating_id UUID PRIMARY KEY,
    episode_id UUID,
    user_id UUID,
    rating_value FLOAT
);

CREATE TABLE reviews (
    review_id UUID PRIMARY KEY,
    episode_id UUID,
    user_id UUID,
    review_text STRING
);
