tables.table_schema:
CREATE TABLE tv_show_genres (
    show_id INT,
    show_title VARCHAR(255),
    genre VARCHAR(50)
);

CREATE TABLE tv_shows (
    id INT PRIMARY KEY,
    name VARCHAR NOT NULL,
    genre VARCHAR NOT NULL,
    rating DECIMAL
);

CREATE TABLE genres (
    id INT PRIMARY KEY,
    genre_name VARCHAR NOT NULL
);

CREATE TABLE TVShows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255),
    genre VARCHAR(50),
    release_date DATE
);

CREATE TABLE Genres (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(50)
);

CREATE TABLE ShowGenres (
    show_id INT,
    genre_id INT,
    FOREIGN KEY (show_id) REFERENCES TVShows(show_id),
    FOREIGN KEY (genre_id) REFERENCES Genres(genre_id)
);

CREATE TABLE ShowGenres (
  show_id INT PRIMARY KEY,
  show_name VARCHAR(255),
  genre VARCHAR(50)
);

CREATE TABLE GenreCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100)
);

CREATE TABLE ShowCategoryMapping (
  show_id INT,
  category_id INT,
  FOREIGN KEY (show_id) REFERENCES ShowGenres(show_id),
  FOREIGN KEY (category_id) REFERENCES GenreCategories(category_id)
);

CREATE TABLE ShowRatings (
  show_id INT,
  rating FLOAT,
  FOREIGN KEY (show_id) REFERENCES ShowGenres(show_id)
);

CREATE TABLE Genres (
  genre_id INT PRIMARY KEY,
  genre_name VARCHAR(50) NOT NULL
);

CREATE TABLE Shows (
  show_id INT PRIMARY KEY,
  show_name VARCHAR(100) NOT NULL,
  genre_id INT,
  FOREIGN KEY (genre_id) REFERENCES Genres(genre_id)
);

CREATE TABLE Actors (
  actor_id INT PRIMARY KEY,
  actor_name VARCHAR(100) NOT NULL
);
