tables.table_schema:
CREATE TABLE viewership_trends (
    viewer_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR,
    location VARCHAR
);

CREATE TABLE viewers (
    viewer_id UUID PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(50)
);

CREATE TABLE trends_analysis (
    trend_id INT PRIMARY KEY,
    demographic_factor VARCHAR,
    trend_description TEXT
);

CREATE TABLE demographic_data (
    viewer_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR,
    location VARCHAR
);

CREATE TABLE viewer_preferences (
    preference_id INT PRIMARY KEY,
    viewer_id INT,
    genre VARCHAR,
    language VARCHAR
);

CREATE TABLE demographic_trends (
    trend_id INT PRIMARY KEY,
    date DATE,
    age_group VARCHAR,
    total_viewers INT
);
