tables.table_schema:
CREATE TABLE TVShows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255),
    production_studio_id INT
);

CREATE TABLE ProductionStudios (
    studio_id INT PRIMARY KEY,
    studio_name VARCHAR(255),
    country VARCHAR(50)
);

CREATE TABLE TV_Shows (
    show_id UUID PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    release_date DATE,
    studio_id UUID
);

CREATE TABLE Studios (
    studio_id UUID PRIMARY KEY,
    studio_name VARCHAR(255) NOT NULL
);

CREATE TABLE Show_Studios (
    show_id UUID,
    studio_id UUID,
    FOREIGN KEY (show_id) REFERENCES TV_Shows(show_id),
    FOREIGN KEY (studio_id) REFERENCES Studios(studio_id),
    PRIMARY KEY (show_id, studio_id)
);

CREATE TABLE tv_shows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255),
    studio_id INT,
    FOREIGN KEY (studio_id) REFERENCES production_studios(studio_id)
);

CREATE TABLE production_studios (
    studio_id INT PRIMARY KEY,
    studio_name VARCHAR(255)
);

CREATE TABLE seasons (
    season_id INT PRIMARY KEY,
    show_id INT,
    season_number INT,
    FOREIGN KEY (show_id) REFERENCES tv_shows(show_id)
);

CREATE TABLE TVShows (
   show_id INT PRIMARY KEY,
   show_name VARCHAR(50),
   release_date DATE,
   studio_id INT
);

CREATE TABLE Studios (
   studio_id INT PRIMARY KEY,
   studio_name VARCHAR(50),
   location VARCHAR(50)
);

CREATE TABLE ShowGenres (
   show_id INT,
   genre VARCHAR(50),
   PRIMARY KEY (show_id, genre)
);

CREATE TABLE StudiosOwnership (
   studio_id INT,
   parent_company VARCHAR(50),
   PRIMARY KEY (studio_id, parent_company)
);

CREATE TABLE ShowRatings (
   show_id INT,
   rating FLOAT,
   PRIMARY KEY (show_id)
);
