    show_id INT PRIMARY KEY,
    show_name VARCHAR(255),
    platform_name VARCHAR(255),
    available BOOLEAN
);

CREATE TABLE platform_info (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(255),
    platform_type VARCHAR(50),
    region VARCHAR(50)
);

