tables.table_schema:
CREATE TABLE episode_engagement (
  episode_id INT PRIMARY KEY,
  views INT,
  likes INT,
  comments INT
);

CREATE TABLE social_media_buzz (
  episode_id INT PRIMARY KEY,
  hashtags VARCHAR(255),
  mentions VARCHAR(255),
  shares INT
);

CREATE TABLE episode_engagement (
    episode_id INT PRIMARY KEY,
    viewership_count INT,
    social_media_buzz_count INT
);

CREATE TABLE viewer_ratings (
    viewer_id INT PRIMARY KEY,
    episode_id INT,
    rating FLOAT,
    FOREIGN KEY (episode_id) REFERENCES episode_engagement(episode_id)
);

CREATE TABLE `viewer_demographics` (
  `viewer_id` INT,
  `age` INT,
  `gender` VARCHAR(10),
  `location` VARCHAR(50)
);

CREATE TABLE episode_viewers (
  user_id INT,
  episode_id INT,
  viewing_time TIMESTAMP,
  user_rating INT
);

CREATE TABLE user_social_media (
  user_id INT,
  social_media_handle VARCHAR,
  followers_count INT,
  following_count INT
);

CREATE TABLE episode_hashtags (
  episode_id INT,
  hashtag VARCHAR
);
