tables.table_schema:
CREATE TABLE tv_shows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    genre VARCHAR(50),
    viewership BIGINT,
    rating FLOAT
);

CREATE TABLE advertising (
    ad_id INT PRIMARY KEY,
    ad_name VARCHAR(100) NOT NULL,
    ad_duration INT,
    ad_cost DECIMAL(10,2)
);

CREATE TABLE tv_show_popularity (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255),
    popularity_rating FLOAT
);

CREATE TABLE viewership_statistics (
    viewer_id INT PRIMARY KEY,
    show_id INT,
    viewership_count INT,
    FOREIGN KEY (show_id) REFERENCES tv_show_popularity(show_id)
);

CREATE TABLE advertising_revenue (
    ad_id INT PRIMARY KEY,
    show_id INT,
    revenue DECIMAL(10,2),
    FOREIGN KEY (show_id) REFERENCES tv_show_popularity(show_id)
);

CREATE TABLE Shows (
    show_id SERIAL PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    genre VARCHAR(50),
    release_year INT
);
CREATE TABLE Viewership (
    viewership_id SERIAL PRIMARY KEY,
    show_id INT REFERENCES Shows(show_id),
    viewership_count INT
);
CREATE TABLE Advertising (
    ad_id SERIAL PRIMARY KEY,
    show_id INT REFERENCES Shows(show_id),
    advertiser_name VARCHAR(100),
    ad_revenue DECIMAL(10, 2)
);
CREATE TABLE Networks (
    network_id SERIAL PRIMARY KEY,
    network_name VARCHAR(100)
);
CREATE TABLE ShowNetworkMapping (
    show_id INT REFERENCES Shows(show_id),
    network_id INT REFERENCES Networks(network_id)
);
