tables.table_schema:
CREATE TABLE BookEvents (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_type VARCHAR(50),
    event_date DATE
);

CREATE TABLE BookEvent (event_id INT PRIMARY KEY, event_name VARCHAR(255) NOT NULL, event_date DATE NOT NULL, location VARCHAR(255) NOT NULL, organizer VARCHAR(255) NOT NULL);

CREATE TABLE AuthorSigning (signing_id INT PRIMARY KEY, event_id INT NOT NULL, author_name VARCHAR(255) NOT NULL, signing_date DATE NOT NULL, location VARCHAR(255) NOT NULL, FOREIGN KEY (event_id) REFERENCES BookEvent(event_id));

CREATE TABLE BookFair (fair_id INT PRIMARY KEY, event_id INT NOT NULL, fair_name VARCHAR(255) NOT NULL, fair_date DATE NOT NULL, location VARCHAR(255) NOT NULL, organizer VARCHAR(255) NOT NULL, FOREIGN KEY (event_id) REFERENCES BookEvent(event_id));

CREATE TABLE BookLaunch (launch_id INT PRIMARY KEY, event_id INT NOT NULL, book_title VARCHAR(255) NOT NULL, launch_date DATE NOT NULL, location VARCHAR(255) NOT NULL, organizer VARCHAR(255) NOT NULL, FOREIGN KEY (event_id) REFERENCES BookEvent(event_id));
