tables.table_schema:
CREATE TABLE schedules (
    schedule_id UUID PRIMARY KEY,
    route_id UUID,
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE routes (
    route_id UUID PRIMARY KEY,
    mode_of_transport VARCHAR,
    total_distance DECIMAL,
    average_speed DECIMAL
);

CREATE TABLE Buses (
    bus_id INT PRIMARY KEY,
    bus_route_id INT,
    bus_schedule TIMESTAMPTZ
);

CREATE TABLE Trains (
    train_id INT PRIMARY KEY,
    train_route_id INT,
    train_schedule TIMESTAMPTZ
);

CREATE TABLE Subways (
    subway_id INT PRIMARY KEY,
    subway_route_id INT,
    subway_schedule TIMESTAMPTZ
);

CREATE TABLE buses (
    bus_id INT PRIMARY KEY,
    bus_route VARCHAR(50),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE trains (
    train_id INT PRIMARY KEY,
    train_route VARCHAR(50),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE subways (
    subway_id INT PRIMARY KEY,
    subway_line VARCHAR(50),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE buses_schedule (
    schedule_id INT PRIMARY KEY,
    bus_number VARCHAR(50) NOT NULL,
    departure_time TIME NOT NULL,
    arrival_time TIME NOT NULL
);

CREATE TABLE trains_schedule (
    schedule_id INT PRIMARY KEY,
    train_number VARCHAR(50) NOT NULL,
    departure_time TIME NOT NULL,
    arrival_time TIME NOT NULL
);

CREATE TABLE subways_schedule (
    schedule_id INT PRIMARY KEY,
    subway_line VARCHAR(50) NOT NULL,
    departure_time TIME NOT NULL,
    arrival_time TIME NOT NULL
);

CREATE TABLE routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(100) NOT NULL,
    mode_of_transport VARCHAR(20) NOT NULL
);

CREATE TABLE stops (
    stop_id INT PRIMARY KEY,
    stop_name VARCHAR(100) NOT NULL,
    location VARCHAR(50) NOT NULL
);
