tables.table_schema:
CREATE TABLE riders (
  rider_id INT PRIMARY KEY,
  rider_name VARCHAR(50),
  rider_rating FLOAT
);

CREATE TABLE riders (
    rider_id INT PRIMARY KEY,
    rider_name VARCHAR,
    rider_email VARCHAR
);

CREATE TABLE rides (
    ride_id INT PRIMARY KEY,
    rider_id INT,
    ride_date DATE,
    ride_distance FLOAT,
    ride_duration FLOAT
);

CREATE TABLE routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR,
    route_distance FLOAT
);

CREATE TABLE schedules (
    schedule_id INT PRIMARY KEY,
    route_id INT,
    day_of_week VARCHAR,
    departure_time TIME
);

CREATE TABLE vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_name VARCHAR,
    vehicle_capacity INT
);
