tables.table_schema:
CREATE TABLE complaints (
  complaint_id SERIAL PRIMARY KEY,
  passenger_id INT NOT NULL,
  complaint_text TEXT,
  complaint_date DATE
);

CREATE TABLE passengers (
  passenger_id SERIAL PRIMARY KEY,
  passenger_name VARCHAR(50) NOT NULL,
  passenger_email VARCHAR(50) UNIQUE,
  passenger_phone VARCHAR(15)
);

CREATE TABLE feedback (
  feedback_id SERIAL PRIMARY KEY,
  passenger_id INT NOT NULL,
  feedback_text TEXT,
  feedback_date DATE
);

CREATE TABLE PassengerComplaints (
  complaint_id UUID PRIMARY KEY,
  passenger_id UUID NOT NULL,
  complaint_text TEXT NOT NULL,
  complaint_date TIMESTAMP NOT NULL
);

CREATE TABLE Feedback (
  feedback_id UUID PRIMARY KEY,
  passenger_id UUID NOT NULL,
  feedback_text TEXT NOT NULL,
  feedback_date TIMESTAMP NOT NULL
);

CREATE TABLE PassengerInfo (
  passenger_id UUID PRIMARY KEY,
  passenger_name VARCHAR NOT NULL,
  email VARCHAR NOT NULL,
  phone_number VARCHAR
);

CREATE TABLE ComplaintResolution (
  resolution_id UUID PRIMARY KEY,
  complaint_id UUID NOT NULL,
  resolution_text TEXT NOT NULL,
  resolution_date TIMESTAMP NOT NULL
);

CREATE TABLE FeedbackRating (
  feedback_id UUID PRIMARY KEY,
  rating INT NOT NULL
);
