tables.table_schema:
CREATE TABLE budget_planning (
    id INT PRIMARY KEY,
    year INT,
    budget DECIMAL,
    expenses DECIMAL
);

CREATE TABLE financial_analysis (
    id INT PRIMARY KEY,
    year INT,
    profit DECIMAL,
    revenue DECIMAL
);

CREATE TABLE BudgetPlans (
    id INT PRIMARY KEY,
    year INT,
    budget_amount FLOAT
);

CREATE TABLE Expenses (
    id INT PRIMARY KEY,
    budget_plan_id INT,
    expense_category VARCHAR(50),
    expense_amount FLOAT
);

CREATE TABLE Income (
    id INT PRIMARY KEY,
    year INT,
    income_amount FLOAT
);

CREATE TABLE FinancialAnalysis (
    id INT PRIMARY KEY,
    budget_plan_id INT,
    analysis_date DATE,
    analysis_result VARCHAR(100)
);

CREATE TABLE BudgetCategories (
    id INT PRIMARY KEY,
    category_name VARCHAR(50),
    category_description TEXT
);
