tables.table_schema:
CREATE TABLE Aircraft (
    registration_number VARCHAR(10) PRIMARY KEY,
    aircraft_type VARCHAR(50) NOT NULL,
    manufacturer VARCHAR(50) NOT NULL,
    last_maintenance_date DATE
);

CREATE TABLE MaintenanceRecord (
    record_id SERIAL PRIMARY KEY,
    registration_number VARCHAR(10) REFERENCES Aircraft(registration_number),
    maintenance_date DATE NOT NULL,
    description TEXT
);

CREATE TABLE Aircraft (
  aircraft_id INT PRIMARY KEY,
  aircraft_type VARCHAR(255),
  registration_number VARCHAR(10),
  manufacturer VARCHAR(255)
);

CREATE TABLE SafetyRegulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(255),
  regulation_description TEXT
);

CREATE TABLE Engineers (
    engineer_id INT PRIMARY KEY,
    engineer_name VARCHAR(50),
    specialization VARCHAR(50),
    years_of_experience INT
);

CREATE TABLE Regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(50),
    description TEXT
);
