tables.table_schema:
CREATE TABLE airports (
    airport_id INT,
    airport_name VARCHAR(255),
    runway_length FLOAT,
    facilities TEXT
);

CREATE TABLE airports (
    airport_id INT PRIMARY KEY,
    airport_name VARCHAR(50) NOT NULL,
    runway_length INT NOT NULL,
    facilities TEXT
);

CREATE TABLE runways (
    runway_id INT PRIMARY KEY,
    runway_name VARCHAR(50) NOT NULL,
    airport_id INT REFERENCES airports(airport_id),
    runway_length INT NOT NULL
);

CREATE TABLE AirportInformation (
    airport_code VARCHAR(3) PRIMARY KEY,
    airport_name VARCHAR(255) NOT NULL,
    runway_length FLOAT NOT NULL
);

CREATE TABLE FacilitiesInformation (
    facility_id SERIAL PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    facility_type VARCHAR(50) NOT NULL
);

CREATE TABLE PilotsInformation (
    pilot_id SERIAL PRIMARY KEY,
    pilot_name VARCHAR(255) NOT NULL,
    pilot_license VARCHAR(10) NOT NULL,
    airport_code VARCHAR(3) REFERENCES AirportInformation(airport_code)
);

CREATE TABLE runways (
  runway_id INT PRIMARY KEY,
  runway_length FLOAT NOT NULL,
  airport_id INT,
  FOREIGN KEY (airport_id) REFERENCES airports(airport_id)
);

CREATE TABLE facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(100) NOT NULL,
  airport_id INT,
  FOREIGN KEY (airport_id) REFERENCES airports(airport_id)
);

CREATE TABLE pilots (
  pilot_id INT PRIMARY KEY,
  pilot_name VARCHAR(100) NOT NULL,
  license_type VARCHAR(50) NOT NULL
);
