tables.table_schema:
CREATE TABLE air_traffic_controllers (
  controller_id INT PRIMARY KEY,
  controller_name VARCHAR(50) NOT NULL,
  controller_rating INT
);

CREATE TABLE airports (
  airport_id INT PRIMARY KEY,
  airport_name VARCHAR(100) NOT NULL,
  airport_location VARCHAR(100),
  airport_type ENUM('International', 'Domestic')
);

CREATE TABLE air_traffic_controllers (
    controller_id INT PRIMARY KEY,
    controller_name VARCHAR(50),
    controller_rank VARCHAR(20),
    controller_area_of_responsibility VARCHAR(50)
);
