tables.table_schema:
CREATE TABLE PilotCertifications (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    certification_type VARCHAR(50) NOT NULL,
    certification_date DATE
);

CREATE TABLE Pilots (
    pilot_id INT PRIMARY KEY,
    full_name VARCHAR(255),
    certification_date DATE,
    certification_type VARCHAR(50)
);

CREATE TABLE RegulatoryRequirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(100),
    requirement_description TEXT
);

CREATE TABLE Pilots (
    pilot_id INT PRIMARY KEY,
    name VARCHAR(50),
    certification VARCHAR(50),
    training_hours INT
);

CREATE TABLE Aircrafts (
    aircraft_id INT PRIMARY KEY,
    aircraft_type VARCHAR(50),
    manufacturer VARCHAR(50),
    year_of_manufacture INT
);

CREATE TABLE Certifications (
    certification_id INT PRIMARY KEY,
    certificate_name VARCHAR(50),
    agency VARCHAR(50),
    valid_until DATE
);

CREATE TABLE Certification (
    id INT PRIMARY KEY,
    certification_name VARCHAR(100),
    authority VARCHAR(100),
    expiry_date DATE
);

CREATE TABLE Training (
    id INT PRIMARY KEY,
    training_name VARCHAR(100),
    duration_hours INT,
    completion_date DATE
);

CREATE TABLE Pilot_Training (
    pilot_id INT,
    training_id INT,
    FOREIGN KEY (pilot_id) REFERENCES Pilot(id),
    FOREIGN KEY (training_id) REFERENCES Training(id)
);
