tables.table_schema:
CREATE TABLE Aircraft (
  aircraft_id VARCHAR PRIMARY KEY,
  aircraft_name VARCHAR,
  model VARCHAR,
  manufacturer VARCHAR
);

CREATE TABLE Parts (
  part_id VARCHAR PRIMARY KEY,
  part_name VARCHAR,
  part_description VARCHAR,
  unit_price DECIMAL
);

CREATE TABLE MaintenanceRecords (
  record_id VARCHAR PRIMARY KEY,
  aircraft_id VARCHAR,
  maintenance_date DATE,
  maintenance_type VARCHAR
);

CREATE TABLE Employees (
  employee_id VARCHAR PRIMARY KEY,
  employee_name VARCHAR,
  position VARCHAR,
  department_name VARCHAR
);

CREATE TABLE aircrafts (
    aircraft_id INT PRIMARY KEY,
    aircraft_name VARCHAR(50),
    aircraft_type VARCHAR(50),
    manufacture_date DATE
);

CREATE TABLE parts (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(50),
    part_type VARCHAR(50),
    quantity INT
);

CREATE TABLE maintenance_records (
    record_id INT PRIMARY KEY,
    aircraft_id INT,
    maintenance_date DATE,
    maintenance_details TEXT
);

CREATE TABLE mechanics (
    mechanic_id INT PRIMARY KEY,
    mechanic_name VARCHAR(50),
    mechanic_specialty VARCHAR(50),
    mechanic_phone VARCHAR(15)
);
