tables.table_schema:
CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(255),
  start_point GEOMETRY(POINT),
  end_point GEOMETRY(POINT)
);

CREATE TABLE ShippingRoutes (
    route_id SERIAL PRIMARY KEY,
    start_location POINT,
    end_location POINT,
    distance_km FLOAT
);

CREATE TABLE ShippingWarehouses (
    warehouse_id SERIAL PRIMARY KEY,
    location POINT,
    capacity INTEGER
);

CREATE TABLE ShippingOrders (
    order_id SERIAL PRIMARY KEY,
    warehouse_id INTEGER,
    route_id INTEGER,
    delivery_date DATE
);

CREATE TABLE shipping_destinations (
  destination_id INT PRIMARY KEY,
  destination_name VARCHAR(255),
  destination_geom GEOGRAPHY
);
