CREATE TABLE TransportationCosts (
	report_id INT PRIMARY KEY,
	report_name STRING,
	transportation_type STRING,
	cost DECIMAL(10,2)
);

CREATE TABLE CostSavingOpportunities (
	opportunity_id INT PRIMARY KEY,
	opportunity_description TEXT,
	cost_saving_amount DECIMAL(10,2),
	implementation_plan TEXT
);

CREATE TABLE transportation_costs (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(255),
    total_cost DECIMAL(10, 2)
);

CREATE TABLE cost_saving_opportunities (
    opportunity_id INT PRIMARY KEY,
    detail_id INT REFERENCES transportation_details(detail_id),
    savings_amount DECIMAL(10, 2),
    recommended_action VARCHAR(500)
);

CREATE TABLE transportation_costs (
    id INT PRIMARY KEY,
