tables.table_schema:
CREATE TABLE WarehouseCapacityUtilization (
  warehouse_id INT PRIMARY KEY,
  total_capacity DECIMAL,
  used_capacity DECIMAL
);

CREATE TABLE StorageLayout (
  shelf_id INT PRIMARY KEY,
  warehouse_id INT,
  shelf_capacity DECIMAL,
  items_count INT
);

CREATE TABLE warehouse_capacity (
	warehouse_id INT PRIMARY KEY,
	capacity INT
);

CREATE TABLE storage_space (
	space_id INT PRIMARY KEY,
	warehouse_id INT,
	capacity_utilization FLOAT
);

CREATE TABLE layout_optimization (
	opt_id INT PRIMARY KEY,
	space_id INT,
	layout_type VARCHAR
);

CREATE TABLE warehouse_capacity (
    warehouse_id INT PRIMARY KEY,
    capacity FLOAT
);

CREATE TABLE product (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(100),
    weight FLOAT
);

CREATE TABLE warehouse_capacity (
    warehouse_id INT PRIMARY KEY,
    warehouse_name VARCHAR(255) NOT NULL,
    warehouse_location VARCHAR(255) NOT NULL
);

CREATE TABLE shelf_details (
    shelf_id INT PRIMARY KEY,
    shelf_number INT NOT NULL,
    shelf_capacity INT NOT NULL,
    warehouse_id INT REFERENCES warehouse_capacity(warehouse_id)
);

CREATE TABLE item_inventory (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    item_quantity INT NOT NULL,
    shelf_id INT REFERENCES shelf_details(shelf_id)
);

CREATE TABLE item_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE item_category_mapping (
    item_id INT REFERENCES item_inventory(item_id),
    category_id INT REFERENCES item_categories(category_id),
    PRIMARY KEY (item_id, category_id)
);
