tables.table_schema:
CREATE TABLE Traffic_Light_Configurations (
    id INT PRIMARY KEY,
    color VARCHAR(10),
    duration INT
);

CREATE TABLE Traffic_Light_Schedules (
    id INT PRIMARY KEY,
    day_of_week VARCHAR(15),
    start_time TIME,
    end_time TIME
);

CREATE TABLE Traffic_Lights_Configurations (
    light_id INT PRIMARY KEY,
    location VARCHAR(50),
    color VARCHAR(20),
    duration INT
);

CREATE TABLE Traffic_Flow (
    flow_id INT PRIMARY KEY,
    street_name VARCHAR(50),
    direction VARCHAR(10),
    vehicle_count INT
);

CREATE TABLE TrafficLightConfigurations (
    id INT PRIMARY KEY,
    light_color VARCHAR(10),
    duration_seconds INT
);

CREATE TABLE TrafficLightSchedules (
    id INT PRIMARY KEY,
    schedule_name VARCHAR(50),
    start_time TIME,
    end_time TIME
);

CREATE TABLE Intersection (
    id INT PRIMARY KEY,
    location VARCHAR(100),
    road_name VARCHAR(50)
);

CREATE TABLE VehicleDetection (
    id INT PRIMARY KEY,
    vehicle_id INT,
    detection_time TIMESTAMP,
    intersection_id INT
);

CREATE TABLE TrafficFlow (
    id INT PRIMARY KEY,
    flow_direction VARCHAR(20),
    flow_rate INT,
    intersection_id INT
);
