tables.table_schema:
CREATE TABLE TrafficData (
    city_id INT,
    date DATE,
    traffic_volume INT,
    congestion_level VARCHAR(255)
);

CREATE TABLE Historical_Traffic_Data (
  traffic_id INT PRIMARY KEY,
  city_name VARCHAR(255),
  date DATE,
  traffic_volume INT
);

CREATE TABLE Traffic_Trends (
    city_id INT,
    year INT,
    traffic_volume INT,
    congestion_level VARCHAR(50)
);

CREATE TABLE City_Planners (
    planner_id INT,
    planner_name VARCHAR(50),
    city_id INT,
    email VARCHAR(100)
);

CREATE TABLE Road_Network (
    road_id INT,
    city_id INT,
    road_name VARCHAR(100),
    road_length FLOAT
);
