tables.table_schema:
CREATE TABLE road_closures (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(255),
    closure_start_date DATE,
    closure_end_date DATE
);

CREATE TABLE road_closures (
    id INT PRIMARY KEY,
    road_name VARCHAR(255),
    closure_start TIMESTAMP,
    closure_end TIMESTAMP
);

CREATE TABLE traffic_agencies (
    id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    contact_phone VARCHAR(15)
);

CREATE TABLE traffic_reports (
    id INT PRIMARY KEY,
    route_id INT,
    report_date DATE,
    report_description TEXT
);
