tables.table_schema:
CREATE TABLE TrafficAnalysis ( 
 intersection_id INT, 
 timestamp TIMESTAMP, 
 vehicle_count INT, 
 average_speed FLOAT 
);

CREATE TABLE traffic_patterns (
    intersection_id INT,
    traffic_volume INT,
    vehicle_type VARCHAR(50),
    average_speed FLOAT
);

CREATE TABLE signal_timings (
    intersection_id INT,
    green_light_duration INT,
    yellow_light_duration INT,
    red_light_duration INT
);

CREATE TABLE Intersection (
    intersection_id INT PRIMARY KEY,
    location VARCHAR(255),
    traffic_flow INT
);
